				transition: transform 200ms cubic-bezier(0.175, 0.885, 0.32, 1.275);
				&:hover {
					transform: scale(1.1);
					cursor: pointer;
					border: 3px solid $primary;
					border-radius: 10px !important;
					filter: drop-shadow(0px 0px 10px rgba(0, 0, 0, 0.9));
				}
			}
		}

		main {
			min-width: 600px;
			height: 100%;
			min-height: 100vh;
			background-color: $background-secondary;
		}

		#message-container {
			display: flex;
			flex-direction: column;
			justify-content: flex-start;
			align-items: flex-start;
			padding: 1em;
			max-height: 80vh;
			min-height: 80vh;
			height: 80%;
			overflow-y: scroll;
			::-webkit-scrollbar {
				width: 0px; /* Remove scrollbar space */
				background: transparent; /* Optional: just make scrollbar invisible */
			}
			overflow: -moz-scrollbars-none;
			scrollbar-width: none;

			.message {
				position: relative;
				background: $primary;
				border-radius: 0.4em;
				min-width: 150px;
				max-width: 300px;
				word-wrap: break-word;
				padding: 1em;
				margin: 15px;
				color: white;
				filter: drop-shadow(0px 5px 3px rgba(0, 0, 0, 0.4));
				&:after {
					content: "";
					position: absolute;
					bottom: 0;
					left: 20%;
					width: 0;
					height: 0;
					border: 1.188em solid transparent;
					border-top-color: $primary;
					border-bottom: 0;
					border-left: 0;
					margin-left: -0.594em;
					margin-bottom: -1.187em;
				}
			}
			.myMessage {
				display: flex;
				flex-direction: column;
				align-self: flex-end;
				min-width: 150px;
				max-width: 300px;
				word-wrap: break-word;
				position: relative;
				background: $secondary;
				border-radius: 0.4em;
				padding: 1em;
				margin: 15px;
				color: white;
				filter: drop-shadow(0px 5px 3px rgba(0, 0, 0, 0.4));
				&:after {
					content: "";
					position: absolute;
					bottom: 0;
					left: 80%;
					width: 0;
					height: 0;
					border: 1.188em solid transparent;
					border-top-color: $secondary;
					border-bottom: 0;
					border-left: 0;
					transform: scale(-1, 1);
					margin-left: -0.594em;
					margin-bottom: -1.187em;
				}
			}
			.statusMessage {
				display: flex;
				flex-direction: row;
				align-items: center;
				justify-content: center;
				width: 100%;
				color: $secondary;
				height: 30px;
				&::before {
					content: "~";
					margin-right: 10px;
				}
				&::after {
					content: "~";
					margin-left: 10px;
				}
			}
		}

		#send-container {
			margin: 1em;
			height: 3.5em;

			display: flex;
			flex-direction: row;
			justify-content: center;
			align-items: center;

			#message-input {
				height: 100%;
				flex-grow: 10;
				padding: 1em;
				background-color: $background-secondary;
				filter: brightness(150%);
				border-radius: 10px 0px 0px 10px;
				border: none;
				color: $message-text;
				font-size: 1em;
				&:focus {
					outline: none;
					border: 2px solid rgba($color: $primary, $alpha: 0.5);
				}
			}
			#send-button {
				height: 100%;
				flex-grow: 1;
				border-radius: 0px 10px 10px 0px;
				margin-right: 1em;
				border: none;
				background-color: $background-secondary-light;
				color: $message-text;
				width: 7em;
				font-size: 0.8em;
				&:hover {
					cursor: pointer;
					border: 3px solid $primary;
					background-color: transparent;
				}
			}
		}
	}
}

